class Solution {
    public boolean isSubsequence(String s, String t) {

        int character = 0;

        if (s.length() <= 0)
            return true;

        for (int i = 0; i < t.length(); i++) {

            if (s.charAt(character) == t.charAt(i)) {

                character++;
                if (s.length() == 1)
                    break;
                if (s.length() == character)
                    break;
            }

        }
        if (character == s.length()) {
            return true;
        } else {
            return false;
        }

    }
}

/**
 * Leetcode392
 */
public class Leetcode392 {

    public static void main(String[] args) {
        Solution solution = new Solution();
        System.out.println(solution.isSubsequence("leeeeetcode",
                "")); // true
    }
}